import { FormControl, InputBase, InputLabel, MenuItem, Select, styled, TextField, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React from 'react'

const FormOS = () => {

    const [age, setAge] = React.useState('');

    const handleChange = (event) => {
        setAge(event.target.value);
    };


    return (
        <Box sx={{ display: 'flex', flexDirection: 'row' }}>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
                <div>
                    <Select
                        value={age}
                        onChange={handleChange}
                        displayEmpty
                        inputProps={{ 'aria-label': 'Without label' }}
                    >
                        <MenuItem value={10}>Samuel Ximenes</MenuItem>
                        <MenuItem value={20}>Hamon Ravick</MenuItem>
                        <MenuItem value={30}>Italo Maciel</MenuItem>
                    </Select>

                    <TextField
                        id="filled-number"
                        label="Number"
                        type="text"
                        InputLabelProps={{
                            shrink: true,
                            sx:{mt: 10}
                        }}
                        variant="outlined"
                    />
                </div>

            </FormControl>
        </Box>
    )
}

export default FormOS
